import React,{useState} from 'react'

function COunter() {
    const [count, setCount] = useState(0);

    const increment =  () => {
        count += 1;
        setCount()
    }  
    const decrement = () => {
        count -= 1;
    } 
  return (
    <div>
        <h1>
            welcome to my counter app
        </h1>
        <p>the counter is : {count}</p>
        <button onClick={increment}>+</button>
        <button onClick={decrement}>-</button>

    </div>
  )
}

export default COunter
